import osfrom flask import Flask, render_template,requestfrom scrapper import Scrapperfrom flask_cors import CORS,cross_originapp=Flask(__name__)# HOME PAGE TO TAKE USER QUERIES@app.route('/')@cross_origin()def hello():    return render_template('index.html')# DISPLAYING THE DATA THAT USER REQUIRED@app.route('/displayImages')@cross_origin()def display():    list_images=os.listdir('static')    print(list_images)    try:        if len(list_images)>0:            return render_template('show.html',list_images=list_images)        else:            print('images are not present')    except Exception as e:        print('No images found',e)        return 'please try with other search keyword'# SEARCH AND DOWNLOAD THE IMAGES INTO THE LOCAL SERVER@app.route('/searchImages',methods=['POST'])@cross_origin()def search():    if request.method=='POST':        keyword=request.form['search_term']        count=int(request.form['frequency'])        s=Scrapper()        list_images = os.listdir('static')        s.delete_images(list_images)        web_url = s.create_url(keyword)        html = s.scrap_html(web_url)        imgs = s.get_imagelist(html)        s.download_images(imgs[1:],keyword,count)        print('hii')    return display()if __name__=='__main__':    app.run(debug=True)